/* This section for tag css */
p {
  word-wrap: break-word; }

body {
  margin: 0px; }

/* ------------------------ */
/* for headers */
.h-tspaced {
  margin-top: 20px; }

.fl-right {
  float: right; }

.card {
  padding: 15px; }

.card-blue {
  background-color: #182848;
  color: white; }

.fw-light {
  font-weight: 100; }

.container {
  width: 960px;
  margin: 0px auto;
  -webkit-box-sizing: border-box;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  padding: 10px; }

.container img {
  max-width: 100%; }

.container-full {
  width: 100%;
  float: left;
  position: relative; }

.container-full-messages {
  display: block;
  margin: 0px auto;
  margin-top: 40px;
  padding: 10px; }

.container-nav-fixed {
  width: 100%;
  margin-top: 60px; }

.t-center {
  text-align: center; }

.img-full {
  width: 100%;
  height: auto; }

.btn_box {
  padding: 10px; }

.btn {
  padding: 5px 15px;
  border-radius: none; }

.btn:hover {
  cursor: pointer; }

.btn-blue {
  background-color: #1c3087;
  color: white;
  border: none; }

.btn-blue:hover {
  background-color: navy;
  cursor: pointer; }

.btn-red {
  background-color: #ff2a2a;
  color: white;
  border: none; }

.btn-red:hover {
  background-color: red;
  cursor: pointer; }

.btn-msg {
  background-color: #1c3fff;
  color: white;
  border: none; }

.btn-msg:hover {
  background-color: #1c2eee;
  cursor: pointer; }

.btn-form {
  margin-top: 15px; }

.btn-submit-css {
  color: white;
  border: none;
  background-color: #1b1b1b; }

.btn-submit-css:hover {
  color: orangered; }

.btn-css-image {
  border-radius: 4px;
  padding: 5px 15px;
  background-color: #005d8d;
  color: white;
  border: none; }

.btn-css-image:hover {
  background-color: #222b49; }

.list-down {
  padding: 0px; }

.list-down li {
  list-style: none;
  margin-bottom: 10px; }

